	(193,'new_appointment_admin_subject','[blog-name]: New appointment scheduled by [client-name]','yes'),
	(194,'new_appointment_admin_body','\r\nHi Admin,\r\n\r\nAppointment details are:\r\n\r\nClient Name: [client-name]\r\nClient Email: [client-email]\r\nClient Phone: [client-phone]\r\nClient Special Instruction: [client-si]\r\n\r\nAppointment For: [service-name]\r\nAppointment Date: [app-date]\r\nAppointment Time: [app-time]\r\nAppointment Status: [app-status]\r\n\r\nView this appointment at admin dashboard.\r\n\r\nBest Regards\r\n[blog-name]\r\n','yes'),
	(195,'new_appointment_client_subject','[blog-name]: Your appointment status is [app-status]','yes'),
	(196,'new_appointment_client_body','\r\nHi [client-name],\r\n\r\nThank you for scheduling appointment with [blog-name].\r\n\r\nYour appointment for [service-name] on [app-date] at [app-time].\r\n\r\nCurrently, your appointment status is [app-status].\r\n\r\nYou will get a confirmation mail once admin accepts the appointment.\r\n\r\nBest Regards\r\n[blog-name]\r\n','yes'),
	(197,'approve_appointment_client_subject','[blog-name]: Your appointment status is [app-status]','yes'),
	(198,'approve_appointment_client_body','\r\nHi [client-name],\r\n\r\nYour appointment has been [app-status] by admin.\r\n\r\nNow, your appointment for [service-name] on [app-date] at [app-time].\r\n\r\nThank you for scheduling appointment with [blog-name].\r\n\r\nBest Regards\r\n[blog-name]\r\n','yes'),
	(199,'cancel_appointment_client_subject','[blog-name]: Your appointment status is [app-status]','yes'),
